import React, { useState } from "react";
import "./SearchBar.css";
import { DateRange } from "react-date-range";
import 'react-date-range/dist/styles.css'; // main css file
import 'react-date-range/dist/theme/default.css'; // theme css file
import { format } from "date-fns";

const SearchBar = () => {
  const [searchQuery, setSearchQuery] = useState("");

  const [date, setDate] = useState([
    {
      startDate: new Date(),
      endDate: new Date(),
      key: "selection",
    },
  ]);

  const onChangeHandler = (e) => {
    e.preventDefault();
    setSearchQuery(e.target.value);
  };

  return (
    <div className="search-bar-container">
      <div className="search-bar-input-container">
        <ion-icon name="airplane-sharp"></ion-icon>
        <input
          className="search-bar-input"
          type="text"
          placeholder="Where are you going?"
          onChange={onChangeHandler}
          value={searchQuery}
        />
      </div>
      <div className="search-bar-date">
        <ion-icon name="calendar-sharp"></ion-icon>
        <div className="search-bar-stay-time">{format(date[0].startDate, "dd/MM/yyyy")} to {format(date[0].endDate, "dd/MM/yyyy")}`</div>
        <DateRange
          editableDateInputs={true}
          onChange={(item) => setDate([item.selection])}
          moveRangeOnFirstSelection={false}
          ranges={date}
          className="date"
        />
      </div>
      <div className="search-bar-details-container">
        <ion-icon name="person-sharp"></ion-icon>
        <div className="search-bar-details">1 dorosły, 0 dzieci, 1 pokój</div>
      </div>
      <div className="search-bar-search-btn">
        <ion-icon name="search"></ion-icon>
      </div>
    </div>
  );
};

export default SearchBar;
