import React, { useState } from "react";
import "./SearchBar.css";

const SearchBar = () => {
  const [searchQuery, setSearchQuery] = useState("");

  const onChangeHandler = (e) => {
    e.preventDefault();
    setSearchQuery(e.target.value);
  };

  return (
    <div className="search-bar-container">
      <div className="search-bar-input">
        <ion-icon name="airplane-sharp"></ion-icon>
        <input
          type="text"
          placeholder="Where are you going?"
          onChange={onChangeHandler}
          value={searchQuery}
        />
      </div>
      <div className="search-bar-date">
        <ion-icon name="calendar-sharp"></ion-icon>
        <div className="search-bar-stay-time">check in - check out</div>
      </div>
      <div className="search-bar-details">
        <ion-icon name="person-sharp"></ion-icon>
        <div>1 dorosły, 0 dzieci, 1 pokój </div>
      </div>
      <button className="search-bar-search-btn">Search</button>
    </div>
  );
};

export default SearchBar;
