import React from "react";
import Footer from "../../components/Footer/Footer";
import NavBar from "../../components/NavBar/NavBar";
import "./HotelDetailsPage.css";

const HotelDetailsPage = () => {
    const hotelPhotos = [
        {
            src: 'https://www.manooi.com/mn17/wp-content/uploads/vague_kensington_1.jpg'
        },
        {
            src: 'http://ej-interiors.co.uk/wp-content/uploads/2019/06/0081_014_07_Master_Bedroom_Master_Draft_24-1024x684.jpg'
        },
        {
            src: 'https://vwartclub.com/media/projects/5579/2.jpg'
        },
        {
            src: 'https://i.pinimg.com/originals/92/35/c1/9235c111c19e12712a7f876fb79e2f2b.jpg'
        },
    ]
  return (
    <div>
      <NavBar />
      <div className="hotel-container">
        <h1 className="hotel-title">Apartment Warsaw</h1>
        <div className="hotel-location-container">
          <ion-icon name="location-sharp"></ion-icon>
          <p className="hotel-location">Warsaw, Grzybowska 47A</p>
        </div>
        <span>Excellent location - 400m from center!</span>
        <div className="hotel-imgs">
            {hotelPhotos.map(photo => (
                <img src={photo.src} alt='' className="hotel-img"/>
            ))}
        </div>
        <div className="hotel-details">
                <div className="hotel-details-text">
                    <h1>Stay in heart of Warsaw!</h1>
                    <p>This Warsaw Apartment is just a 10-minute walk from Warsaw Central Station. Free WiFi is provided.
The spacious and modern rooms at the Radisson Collection come with a safety deposit box and a large work desk. All have a large, modern bathroom with a hairdryer.
Radisson Collection Hotel, Warsaw houses the TAGO Restaurant where breakfast buffet is served, and THE SPOT Restaurant and Bar. It's an all-seasons restaurant offering modern cuisine and stylish cocktails. During the summer guests can dine and relax on an outdoor terrace.
The hotel is located just a 10-minute walk from ZÅ‚ote Tarasy Shopping Center and the Palace of Culture & Science. Warsaw Chopin Airport is just a 20-minute drive away.

This is our guests' favorite part of Warsaw, according to independent reviews</p>

                </div>
                <div className="hotel-details-text"></div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default HotelDetailsPage;
